package dmytro.shop.controller;import dmytro.shop.dto.request.CategoryRequest;import dmytro.shop.dto.response.CategoryResponse;import dmytro.shop.service.CategoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import javax.validation.Valid;import java.util.List;@CrossOrigin@RestController@RequestMapping("/category")public class CategoryController {    @Autowired    private CategoryService categoryService;    @PostMapping    public CategoryResponse save(@Valid @RequestBody CategoryRequest categoryRequest) {        return categoryService.save(categoryRequest);    }    @GetMapping    public List<CategoryResponse> findAll() {        return categoryService.findAll();    }    @DeleteMapping    public void delete(@RequestParam Long id) {        categoryService.delete(id);    }    @PutMapping    public CategoryResponse update(@RequestParam Long id,@Valid @RequestBody CategoryRequest categoryRequest) {        return categoryService.update(categoryRequest, id);    }}