package dmytro.shop.repository;import dmytro.shop.entiti.SubCategories;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface SubCategoriesRepository extends JpaRepository<SubCategories, Long> {    @Query ("from SubCategories c where c.name like ?1")    List<SubCategories> fbnlike (String name);}